    ,#{limit,jdbcType=INTEGER}

SELECT a.create_date, b.author, b.topic_id, b.title, a.reply_content, c.avatar from root_reply a,root_topic b,root_user c where a.topic_id = b.topic_id and a.reply_author_id = c.user_id and a.reply_author_name = #{replyAuthorName} ORDER BY a.create_date desc
limit #{start,jdbcType=INTEGER}
,#{limit,jdbcType=INTEGER}

SELECT

a.*,
b.avatar
FROM
root_reply a,root_user b
where a.topic_id = #{topicId}
and a.reply_author_id = b.user_id
order by a.create_date

  limit #{start,jdbcType=INTEGER}

    ,#{limit,jdbcType=INTEGER}

select count(reply_id)
from root_reply t
where t.is_delete = 0 and t.topic_id = #{topicId}

select count(reply_id)
from root_reply t
where t.is_delete = 0 and t.reply_author_id = #{userId}

select count(1)
from root_reply t
where t.is_delete = 0 and t.reply_author_name = #{name}
